import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';


const Info = (props) => (
    <Layout>
        <div>

            <h1>{props.details[0].name}</h1>

                <p className='details-list'>Capital: {props.details[0].capital}</p>
                <p className='details-list'>Currency: {props.details[0].currencies}</p>
                <p className='details-list'>Region: {props.details[0].region}</p>
                <p className='details-list'>TimeZones: {props.details[0].timezones}</p>
                <p className='details-list'>Population: {props.details[0].population}</p>

        </div>
    </Layout>
);

Info.getInitialProps = async function (context) {
    const { name } = context.query;

    const res = await fetch(`https://restcountries.eu/rest/v2/name/${name}?fullText=true`)
    const data = await res.json();

    return {
        details: data
    }
}
export default Info;
