import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';


const Info = (props) => (
    <Layout>
    {console.log(props.details)}
    </Layout>
);

Info.getInitialProps = async function(context) {
    const name = "albania;"
    console.log(name)
    const res = await fetch(`https://restcountries.eu/rest/v2/name/aruba?fullText=true`)
    const data = await res.json();

    return {
        details: data
    }
} 
export default Info;
