import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';


const Info = (props) => (
    <Layout>
        <h1>{props.details.name}</h1>
        <ul>
            <li className='details-list'>Capital: {props.details.capital}</li>
            <li className='details-list'>Currency: {props.details.currencies[0].name}</li>
            <li className='details-list'>Region: {props.details.region}</li>
            <li className='details-list'>TimeZones: {props.details.timezones}</li>
            <li className='details-list'>Population: {props.details.population}</li>
        </ul>
    </Layout>
);

Info.getInitialProps = async function (context) {
    const { name } = context.query;
    const res = await fetch(`https://restcountries.eu/rest/v2/name/${name}?fullText=true`)
    const data = await res.json();

    return {
        details: data
    }
}
export default Info;
