import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';
import NextLink from 'next/link';

const Landing = (props) => (
    <Layout>
        <div>
            <h1>You Are Just One Click Away from A Fun Fact</h1>
            {props.countries.map((countries) => (
                <>
                    <ul id='items-list'>
                        <li id='list-items' key={countries.population}>
                            <NextLink as={`/p/${countries.alpha2Code}`} href={`info?name=${countries.name.toLowerCase()}`}>
                                <a id='items'>{countries.name}</a>
                            </NextLink>
                        </li>
                    </ul>
                </>
            ))}
        </div>
        <style jsx>{`
           #items {
               text-decoration: none;
               color: black;
               font-size: 26px;
           } 
           #items-list {
               margin-top: 50px;
           }
           #items:hover {
               color: gray;
               text-decoration: underline;
               border-right: 10px solid black;
               padding-right: 10px; 
           }
           #items:visited {
               color: purple;
           }
           #list-items {
               list-style-type: circle;

           }
       `}</style>
    </Layout>
)

Landing.getInitialProps = async function () {
    const res = await fetch(`https://restcountries.eu/rest/v2/all`);
    const data = await res.json();

    return {
        countries: data
    }
}

export default Landing;
