import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';
import NextLink from 'next/link';

const Landing = (props) => (
    <Layout>
        <h1>You Are Just One Click Away from A Fun Fact</h1>
        {props.countries.map((countries) => (
            <>
            <ul>
                <li key={countries.population}>
                    <NextLink as={`/p/${countries.alpha2Code}`} href={`post?name=${country.name.toLowerCase()}`}>
                        <a>{countries.name}</a>
                    </NextLink>
                </li>
            </ul>
            </>
        ))}
        {console.log(props.countries)}
    </Layout>
)

Landing.getInitialProps = async function() {
    const res = await fetch(`https://restcountries.eu/rest/v2/all`);
    const data = await res.json();

    return {
        countries: data
    }
}

export default Landing;
