import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';


const Info = (props) => (
    <Layout>

        {props.details.map(details => (
            <>
                <h1>{details.name}</h1>
                <ul>
                    <li className='details-list'>Capital: {details.capital}</li>
                    {/* <li className='details-list'>Currency: {details.currencies}</li>
                    <li className='details-list'>Region: {details.region}</li>
                    <li className='details-list'>TimeZones: {details.timezones}</li>
                    <li className='details-list'>Population: {details.population}</li> */}
                </ul>
            </>
        ))}


    </Layout>
);

Info.getInitialProps = async function (context) {
    const { name } = context.query;

    const res = await fetch(`https://restcountries.eu/rest/v2/name/${name}?fullText=true`)
    const data = await res.json();

    return {
        details: data
    }
}
export default Info;
