import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';


const Info = (props) => (
    <Layout>
        <div>

            <h1>{props.details[0].name}</h1>
            {/* <ul>
            <li className='details-list'>Capital: {props.details[0].capital}</li>
            <li className='details-list'>Currency: {props.details[0].currencies}</li>
            <li className='details-list'>Region: {props.details[0].region}</li>
            <li className='details-list'>TimeZones: {props.details[0].timezones}</li>
            <li className='details-list'>Population: {props.details[0].population}</li>
        </ul> */}
        </div>
    </Layout>
);

Info.getInitialProps = async function (context) {
    const { name } = context.query;

    const res = await fetch(`https://restcountries.eu/rest/v2/name/${name}?fullText=true`)
    const data = await res.json();

    return {
        details: data
    }
}
export default Info;
